<?php

namespace Pi2\Fractalia\Entity\SGSD;

use Doctrine\ORM\EntityRepository;

/**
 * IncidenciaRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class InfoAdjuntoRepository extends EntityRepository
{

    public function removeInfoAdjuntoByIncidenciaId($id)
    {
        $deleteQuery = $this->getEntityManager()->createQuery(
                'DELETE FROM Pi2\Fractalia\Entity\SGSD\InfoAdjunto ia 
                WHERE ia.incidencia = :id')
            ->setParameters(array('id' => $id));
        return $deleteQuery->execute();
    }

}
